rss.channel:
    {{ if eq .Title .Site.Title }}{{ .Site.Title }}{{ else }}{{ with .Title }}{{ . }} on {{ end }}{{ .Site.Title }}{{ end }}
    {{ .Site.BaseURL }}
    Recent content {{ if ne .Title .Site.Title }}{{ with .Title }}in {{ . }} {{ end }}{{ end }}on {{ .Site.Title }}
    Hugo
    {{ site.Language.LanguageCode }}{{ with $authorEmail }}
    {{.}}{{ with $authorName }} ({{ . }}){{ end }}{{ end }}{{ with $authorEmail }}
    {{ . }}{{ with $authorName }} ({{ . }}){{ end }}{{ end }}{{ with .Site.Copyright }}
    {{ . }}{{ end }}{{ if not .Date.IsZero }}
    {{ (index $pages.ByLastmod.Reverse 0).Lastmod.Format "Mon, 02 Jan 2006 15:04:05 -0700" | safeHTML }}{{ end }}
    {{- with .OutputFormats.Get "RSS" }}
    {{ printf "" .Permalink .MediaType | safeHTML }}
    {{- end }}
    {{- range $pages }}


      {{ .Title }}
      {{ .Permalink }}
      {{ .PublishDate.Format "Mon, 02 Jan 2006 15:04:05 -0700" | safeHTML }}
      {{- with $authorEmail }}{{ . }}{{ with $authorName }} ({{ . }}){{ end }}{{ end }}
      {{- range .Params.tags }}
      {{ . }}
      {{- end }}
      {{ .Permalink }}
      {{- $content := .Content }}
      {{- $content = $content | replaceRE `(]+src=")(/[^"]+)` (printf `${1}%s${2}` site.BaseURL) }}
      {{- $content = $content | replaceRE `(]+src=")([^/"h][^"]*)` (printf `${1}%s${2}` .Permalink) }}
      {{- $content = $content | replaceRE `(href=")(#[^"]+)` (printf `${1}%s${2}` .Permalink) }}
      {{- $content = print $content `
— § —
` }}
      {{- if .Params.email | default true }}
        {{- $content = print $content (printf `Reply via email` $authorEmail .Title) }}
      {{- end }}
      {{- if .Params.tags }}
        {{- if .Params.email | default true }}
          {{- $content = print $content ` • ` }}
        {{- end }}
        {{- $content = print $content `Tags: ` }}
        {{- range $index, $tag := .Params.tags }}
          {{- if gt $index 0 }}{{ $content = print $content `, ` }}{{ end }}
          {{- $content = print $content (printf `%s` (strings.TrimSuffix site.BaseURL "/") (lower $tag) $tag) }}
        {{- end }}
      {{- end }}
      {{- $content = print $content `` }}
      {{ $content | transform.XMLEscape | safeHTML }}

    {{- end }}
